/* URLChange.js  https://github.com/1c7/URLChange.js  Author: Cheng Zheng */function addORchangeURLParameter(r,e,a){var t=a.split("?");var n=getParameterFromURL(r,a);if(n){return changeURLParameter(r,e,a)}else{return addURLParameter(r,e,a)}}function addURLParameter(r,e,a){var t=a.split("?");var n=false;if(t[1]){n=true}if(n){var i=t[1].split("&");i.push(r+"="+e);return t[0]+"?"+i.join("&")}else{return t[0]+"?"+r+"="+e}}function changeURLParameter(r,e,a){var t=a.split("?");if(!t[1]){return false}var n=false;var i=t[1].split("&");for(var f=0;f<i.length;f++){var l=i[f].split("=");if(l[0]==r){n=true;l[1]=e;i[f]=l.join("=");var s=t[0]+"?"+i.join("&");return s}}if(n==false){return false}}function getParameterFromURL(r,e){var a=e.split("?");if(!a[1]){return false}var t=false;var n=a[1].split("&");for(var i=0;i<n.length;i++){var f=n[i].split("=");if(f[0]==r){return f[1];t=true}}if(t==false){return false}}function removeParameterFromURL(r,e){var a=e.split("?");if(!a[1]){return false}var t=a[1].split("&");for(var n=0;n<t.length;n++){var i=t[n].split("=");if(i[0]==r){var f=t.indexOf(t[n]);if(f>-1){t.splice(f,1)}}}if(t.length==0){return a[0]}else{return a[0]+"?"+t.join("&")}}function clearParameterFromURL(r){var e=r.split("?");if(!e[1]){return r}return e[0]}function thereAreParameterInURL(r){var e=r.split("?");if(!e[1]){return false}return true}